import React, { useState, useEffect } from 'react'
import FormularioFilme from "../../components/formularioFilme/FomularioFilme"
import ListaFilmes from "../../components/listaFilmes/listafilmes"
import CardFilme from "../../components/cardfilme/cardfilme";

const CatalogoFilmes = () => {
    const [filmes, setFilmes] = useState([])
    const [novoFilme, setNovoFilme] = useState({
        titulo: "",
        descricao: "",
        imagem: "",
    });
    const [mostrarFormulario, setMostrarFormulario] = useState(false);
    const [termoPesquisa, setTermoPesquisa] = useState('');
    const [mensagem, setMensagem] = useState('');

    useEffect(() => {
        const filmesIniciais = [
            {
                id: 1,
                titulo: 'A origem',
                descricao: 'Um ladrão que invade sonhos recebe a tarefa inversa de plantar uma ideia.',
                imagem: 'data:image/jpeg;base64,',
            },
            {
                id: 2,
                titulo: 'Interstellar',
                descricao: 'Uma equipe de exploradores viaja através de um buraco de minhoca no espaço.',
                imagem: 'data:image/jpeg;base64,',
            },
            {
                id: 3,
                titulo: 'Matrix',
                descricao: 'Um hacker descobre a realidade de sua existência.',
                imagem: 'data:image/jpeg;base64,',
            }
        ]
        setFilmes(filmesIniciais)
    }, []) // 

    return (
        <div>
            {}
            {}
            {mostrarFormulario && <FormularioFilme />}
            <ListaFilmes filmes={filmes} />
        </div>
    )
}

export default CatalogoFilmes